INSERT INTO `board_note` VALUES (1, '顾客至上标签大全', 1, NULL, '<p><span style=\"color: rgb(74, 74, 74); font-family: BlinkMacSystemFont, -apple-system, &quot;Segoe UI&quot;, Roboto, Oxygen, Ubuntu, Cantarell, &quot;Fira Sans&quot;, &quot;Droid Sans&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(245, 245, 245);\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;一年一度📅315，每年315的晚会📺可是让各位老板🕴️和各个公司的PR们👨‍💼👩‍💼紧张😨的不得了哇。国际消费者权益日是1983年开始的固定在每年3月15日的国际性🗺️纪念日📆，目标旨在保护🛡️全球消费者👛的权益。虽然商家们🏬总是高唱着“顾客至上🙇”的原则🤲，但是在小编看来消费者们仍然是相对弱势🌱的，他们虽可以用脚投票🙆🙅👍，但是他们仍然付出了成本💸和精力⏳。所以这一期，小编整理了些和消费🛍️相关的表情符号，小伙伴们，如果在消费🛒的过程中遭受到了不平等🤕的待遇😡，小编觉得一定要大声的说出来🗣️📢🆘👊！</span><br></p>', '一年一度📅315', '-1', '0', '0', 'admin', '2022-02-26 09:34:46', 'admin', '2022-03-09 12:16:50', NULL);
