mapper.update:
update mto_post
set views = views + 1
where id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT p.id, p.author_id, p.channel_id, p.comments, p.create_time, p.favors, p.featured, p.STATUS, IFNULL(p.summary,"暂无摘要") as summary, p.tags, p.title, p.views, p.weight, ifnull( p.update_time, p.create_time ) AS update_time, c.name as channelName, IF(ISNULL(p.thumbnail) || LENGTH(trim(p.thumbnail))=0, CONCAT('/blog/img/',(SELECT FLOOR(RAND()*22)),'.jpg'), p.thumbnail) as thumbnail FROM mto_post p LEFT JOIN mto_channel c ON p.channel_id = c.id
and p.status = 0
and category_id = #{mtoPost.categoryId,jdbcType=BIGINT}
and channel_id = #{mtoPost.channelId,jdbcType=BIGINT}
and slider = #{mtoPost.slider,jdbcType=INTEGER}
and title like concat("%",concat(#{mtoPost.title,jdbcType=VARCHAR},"%"))
order by create_time desc

mapper.update:
update mto_post set

    favors = favors+1

where id = #{postId,jdbcType=BIGINT}
